import React, { useState, useEffect } from "react";

const questions = [
  {
    number: 1,
    question: "How many bytes are required to store `kaist` and `\"hello\\n\"` in the C program on 64-bit Linux?",
    choices: ["11", "12", "15", "16"],
    answer: "16",
    explanation: "‚Äòkaist‚Äô = 5 + 1 (null) = 6 bytes, 'hello\\n' = 6 + 1 = 7 bytes ‚Üí total = 13, but with alignment and memory behavior on Linux, stored as 16 bytes.",
    image: "/Users/seoyunbaek/cs230-midterm-tailwind-react/public/images/p1.png"
  },
  {
    number: 2,
    question: "In which area of virtual memory is the variable `kaist` stored?",
    choices: ["Text", "Data", "Heap", "Stack"],
    answer: "Stack",
    explanation: "Local variables like 'kaist' declared inside functions are stored on the stack.",
    image: "/images/p2.png"
  },
  {
    number: 3,
    question: "Where is the dynamically linked `printf()` machine code stored?",
    choices: ["Text", "Kernel area", "Shared Library", "Stack"],
    answer: "Shared Library",
    explanation: "printf is in the C standard library which is linked at runtime as a shared object.",
    image: "/images/p3.png"
  },
  {
    number: 4,
    question: "In which register is the address of the first argument `\"hello\\n\"` stored?",
    choices: ["rsi", "rdi", "rsp", "rax"],
    answer: "rdi",
    explanation: "In x86-64 Linux ABI, the first argument is passed in the %rdi register.",
    image: "/images/p4.png"
  },
  {
    number: 10,
    question: "What is the name of the memory element shown as a D latch circuit diagram?",
    choices: ["R-S Latch", "D Latch", "D Flip-Flop", "T Flip-Flop"],
    answer: "D Latch",
    explanation: "D latch holds the value of D when clock is high.",
    image: "/images/p10.png"
  },
  {
    number: 11,
    question: "To keep the outputs of an R-S latch unchanged, what values should be assigned to R and S?",
    choices: ["0 and 0", "0 and 1", "1 and 0", "1 and 1"],
    answer: "0 and 0",
    explanation: "An R-S latch retains its previous state when both R and S are 0.",
    image: "/images/p11.png"
  },
  {
    number: 17,
    question: "Which byte ordering is used for 0x01234567 stored as 01 23 45 67?",
    choices: ["MSB Endian", "LSB Endian", "Big Endian", "Little Endian"],
    answer: "Big Endian",
    explanation: "Big Endian stores the most significant byte first.",
    image: "/images/p17.png"
  },
  {
    number: 19,
    question: "What is the purpose of the instruction `addq univ(, %rdi, 8), %rsi` in get_univ_digit()?",
    choices: [
      "It adds the offset for digit access",
      "It accesses the digit directly",
      "It computes the base address of univ[index]",
      "It performs a multiplication with 8"
    ],
    answer: "It computes the base address of univ[index]",
    explanation: "This is a scaled indexed address expression: univ + rdi * 8 gives base address.",
    image: "/images/p19.png"
  },
  {
    number: 20,
    question: "After callq 400550, what are the values of RIP, RSP, and M[RSP]?",
    choices: [
      "RIP=400544, RSP=120, M[RSP]=400549",
      "RIP=400550, RSP=118, M[RSP]=400549",
      "RIP=400550, RSP=120, M[RSP]=400544",
      "RIP=400549, RSP=118, M[RSP]=400550"
    ],
    answer: "RIP=400550, RSP=118, M[RSP]=400549",
    explanation: "callq pushes return address (400549) and jumps to 400550. Stack pointer decrements by 8.",
    image: "/images/p20.png"
  },
  {
    number: 23,
    question: "What is the binary of TMax in 6-bit two's complement?",
    choices: ["011111", "111111", "100000", "010101"],
    answer: "011111",
    explanation: "TMax = 2^(6-1)-1 = 31 ‚Üí 011111 in binary.",
    image: "/images/p23.png"
  },
  // ÎÇòÎ®∏ÏßÄ Î¨∏Ï†úÎì§ÎèÑ ÎèôÏùºÌïú Ìè¨Îß∑ÏúºÎ°ú Ï∂îÍ∞Ä
];

export default function CS230MidtermSimulator() {
  const [current, setCurrent] = useState(0);
  const [score, setScore] = useState(0);
  const [selected, setSelected] = useState(null);
  const [done, setDone] = useState(false);
  const [started, setStarted] = useState(false);
  const [timeLeft, setTimeLeft] = useState(70 * 60);
  const shuffled = [...questions].sort(() => Math.random() - 0.5);
  const [shuffledQuestions] = useState(shuffled);

  const currentQ = shuffledQuestions[current];

  useEffect(() => {
    if (started && !done && timeLeft > 0) {
      const timer = setInterval(() => setTimeLeft((t) => t - 1), 1000);
      return () => clearInterval(timer);
    } else if (timeLeft === 0) {
      setDone(true);
    }
  }, [started, timeLeft, done]);

  const handleSelect = (choice) => setSelected(choice);

  const handleSubmit = () => {
    if (selected === currentQ.answer) setScore(score + 1);
    if (current + 1 === shuffledQuestions.length) setDone(true);
    else {
      setCurrent(current + 1);
      setSelected(null);
    }
  };

  const formatTime = (s) => {
    const m = Math.floor(s / 60).toString().padStart(2, "0");
    const sec = (s % 60).toString().padStart(2, "0");
    return `${m}:${sec}`;
  };

  if (!started) {
    return (
      <div className="max-w-2xl mx-auto p-6 text-center">
        <h1 className="text-3xl font-bold mb-6">üß† CS230 Midterm Simulation</h1>
        <p className="mb-4">Ï¥ù {questions.length}Î¨∏Ï†ú, Ï†úÌïúÏãúÍ∞Ñ 1ÏãúÍ∞Ñ 10Î∂Ñ</p>
        <button
          onClick={() => setStarted(true)}
          className="px-6 py-3 bg-blue-600 text-white rounded text-lg"
        >
          Start Test
        </button>
      </div>
    );
  }

  return (
    <div className="max-w-2xl mx-auto p-6 text-left">
      <div className="flex justify-between items-center mb-4">
        <h1 className="text-xl font-bold">CS230 Midterm Simulation</h1>
        <span className="font-mono text-red-600">‚è± {formatTime(timeLeft)}</span>
      </div>

      {!done ? (
        <div>
          <p className="mb-4 font-medium">
            <span className="text-blue-500">Question {currentQ.number}:</span> {currentQ.question}
          </p>
          {currentQ.image && (
            <img
              src={currentQ.image}
              alt={`Q${currentQ.number}`}
              className="mb-4 border rounded max-w-full"
            />
          )}
          <ul className="space-y-2 mb-4">
            {currentQ.choices.map((choice, i) => (
              <li
                key={i}
                className={`border p-2 rounded cursor-pointer hover:bg-gray-100 ${
                  selected === choice ? "bg-blue-100 border-blue-400" : ""
                }`}
                onClick={() => handleSelect(choice)}
              >
                {choice}
              </li>
            ))}
          </ul>
          <button
            onClick={handleSubmit}
            disabled={selected === null}
            className="px-4 py-2 bg-blue-600 text-white rounded disabled:bg-gray-300"
          >
            {current + 1 === shuffledQuestions.length ? "Finish" : "Next"}
          </button>
        </div>
      ) : (
        <div className="text-center">
          <h2 className="text-xl font-bold mb-2">‚úÖ Simulation Complete!</h2>
          <p className="text-lg mb-4">Your score: {score} / {shuffledQuestions.length}</p>
          <p className="mb-2 text-sm text-gray-500">
            ({Math.round((score / shuffledQuestions.length) * 100)}% correct)
          </p>
          <h3 className="text-md font-semibold mt-6 mb-2">üìò Correct Answers & Explanations:</h3>
          <ul className="text-sm text-left">
            {shuffledQuestions.map((q) => (
              <li key={q.number} className="mb-4">
                <strong>Q{q.number}:</strong> {q.answer}<br />
                {q.image && (
                  <img
                    src={q.image}
                    alt={`Q${q.number}`}
                    className="my-2 border rounded max-w-full"
                  />
                )}
                <span className="text-gray-700">üí° {q.explanation || "No explanation provided."}</span>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}
